package com.test.money.transfer.model;

import lombok.Data;

/**
 * Client. Email must be unique (database constraint).
 */
@Data
public class Client {

  /**
   * Id client in database (auto generated by the database).
   */
  private Integer id;
  /**
   * Name of the client.
   */
  private String name;
  /**
   * Email of the client.
   */
  private String email;
}
